  --sidebar-primary: 200 30% 20%;

  --sidebar-primary-foreground: 45 15% 97%;

  --sidebar-accent: 200 15% 85%;

  --sidebar-accent-foreground: 200 30% 20%;

  --sidebar-ring: 200 30% 20%;

  --popover: 45 15% 91%;

  --popover-foreground: 220 15% 25%;

  --popover-border: 45 12% 86%;

  --primary: 200 30% 20%;

  --primary-foreground: 45 15% 97%;

  --secondary: 45 15% 89%;

  --secondary-foreground: 220 15% 25%;

  --muted: 45 8% 88%;

  --muted-foreground: 220 15% 45%;

  --accent: 25 20% 90%;

  --accent-foreground: 220 15% 25%;

  --destructive: 0 75% 35%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 15% 65%;
  --ring: 200 30% 20%;
  --chart-1: 200 50% 25%;
  --chart-2: 35 70% 45%;
  --chart-3: 25 60% 50%;
  --chart-4: 180 35% 30%;
  --chart-5: 280 45% 35%;

  --font-sans: 'Poppins', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(220 15% 25% / 0.05);
  --shadow-xs: 0px 2px 0px 0px hsl(220 15% 25% / 0.08);
  --shadow-sm: 0px 2px 0px 0px hsl(220 15% 25% / 0.05), 0px 1px 2px -1px hsl(220 15% 25% / 0.1);
